function show_question_info(ex) {
  $("#example-display").removeClass("d-none");
  let who = '<span class="text-secondary">' + ex['person'] + '</span>';
  let venue = '<span class="text-secondary">' + ex['venue'] + '</span>';
  $("#claim-display").html(
    who + " " + venue + " " + ex["claim"]
  );
    // .append($('<span>').text(ex["who"]))
    // .append($('<span>').text(ex["when_where"]));
    // .text(ex["claim"]);
  $("#label-display").text(ex["label"]);
  $("#question-set-a").empty();
  $("#question-set-b").empty();
  for(const q of ex["annotations"][0]["questions"]){
    let p = $('<p class="p-2 m-1">').text(q);
    $("#question-set-a").append(p);
  }
  for(const q of ex["annotations"][1]["questions"]){
    let p = $('<p class="p-2 m-1">').text(q);
    $("#question-set-b").append(p);
  }
};

function create_questions_list(data) {
  let filter_type = $("input[name=filter]:checked").val();
  $("#questions-list").empty();
  for(const [k, v] of Object.entries(data)){
    if (filter_type === 'all') {
      $("#questions-list").append('<button class="alert alert-info question-item text-start shadow container-fluid py-2 my-2">' + k + '</button>');
    }
    else{
      if (filter_type === v['label']){
        $("#questions-list").append('<button class="alert alert-info question-item text-start shadow container-fluid py-2 my-2">' + k +   '</button>');
      }
    }
  }
  $(".question-item").click(function() {
    show_question_info(data[$(this).text()]);
  });
  //
  // Object.keys(data).map(ex => {
  //   if ((contra_filter == "none"
  //     || (contra_filter == "nontra" && !data[ex]["contra_pair"]))
  //     || (contra_filter == "contra" && data[ex]["contra_pair"])
  //     && ((label_filter == "none")
  //       || (label_filter == data[ex]["label"])
  //       || (label_filter == data[ex]["label"]))) {
  //     $("#questions-list").append('<button class="alert alert-info question-item text-start shadow container-fluid py-2 my-2">' + ex +'</button>');
  //   }
  // });
  // $(".question-item").click(function() {
  //   show_question_info(data[$(this).text()]);
  // });
};

function shape_window() {
  // $("body").css("overflow", "hidden");
  // $("main").css("body", $(window).height());
  let navbar_height = $("#navbar").outerHeight();
  let filter_height = $("#filter-cell").outerHeight();
  $("#questions-list-cell").css("height", $(window).height() - navbar_height - filter_height);
  $("#question-display").css("height", $(window).height() - navbar_height);
};

$(document).ready(function() {
  // var data = $.getJSON("src/data.json", function(data) {
  var data = load_data();
  shape_window();
  create_questions_list(data);
  // $("input[name=contra_filter]").click(function() {
  //   console.log($(this).val())
  //   create_questions_list(data);
  // });
  $("input[name=filter]").click(function() {create_questions_list(data)});
});

